import _ from 'lodash'
import MagisterThing from './magisterThing'
import Appointment from './appointment'
import { toString, parseDate } from './util'

/**
 * @extends MagisterThing
 */
class AbsenceInfo extends MagisterThing {
	/**
	 * @private
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @type {Number}
		 * @private
		 * @readonly
		 */
		this._type = raw.Verantwoordingtype

		/**
		 * @type {String}
		 * @readonly
		 */
		this.id = toString(raw.Id)
		/**
		 * @type {Date}
		 * @readonly
		 */
		this.begin = parseDate(raw.Start)
		/**
		 * @type {Date}
		 * @readonly
		 */
		this.end = parseDate(raw.Eind)
		/**
		 * @type {Number}
		 * @readonly
		 */
		this.schoolHour = raw.Lesuur
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.isPermitted = raw.Geoorloofd
		/**
		 * @type {String}
		 * @readonly
		 */
		this.description = _.toString(raw.Omschrijving).trim()
		/**
		 * @type {String}
		 * @readonly
		 */
		this.code = _.toString(raw.Code)
		/**
		 * @type {Appointment}
		 * @readonly
		 */
		this.appointment = new Appointment(magister, raw.Afspraak) // REVIEW: do we want (and need) this?
	}

	/**
	 * @type {String}
	 * @readonly
	 */
	get type() {
		switch (this._type) {
		case 1:  return 'absent'
		case 2:  return 'late'
		case 3:  return 'sick'
		case 4:  return 'discharged'
		case 6:  return 'exemption'
		case 7:  return 'books'
		case 8:  return 'homework'

		default: return 'unknown'
		}
	}
}

export default AbsenceInfo

import MagisterThing from './magisterThing'
import ActivityElement from './activityElement'
import { cleanHtmlContent, parseDate, toString } from './util'

/**
 * @extends MagisterThing
 */
class Activity extends MagisterThing {
	/**
	 * @private
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @type {String}
		 * @readonly
		 */
		this.id = toString(raw.Id)
		/**
		 * @type {String}
		 * @readonly
		 */
		this.title = raw.Titel
		/**
		 * @type {String}
		 * @readonly
		 */
		this.description = cleanHtmlContent(raw.Details)

		/**
		 * @type {Date}
		 * @readonly
		 */
		this.signinStart = parseDate(raw.StartInschrijfdatum)
		/**
		 * @type {Date}
		 * @readonly
		 */
		this.signinEnd = parseDate(raw.EindeInschrijfdatum)

		/**
		 * @type {Date}
		 * @readonly
		 */
		this.visibleFrom = parseDate(raw.ZichtbaarVanaf)
		/**
		 * @type {Date}
		 * @readonly
		 */
		this.visibleTo = parseDate(raw.ZichtbaarTotEnMet)

		/**
		 * @type {String}
		 * @private
		 * @readonly
		 */
		this._url = `${magister._personUrl}/activiteiten/${this.id}`
	}

	/**
	 * @return {Promise<ActivityElement[]>}
	 */
	elements() {
		const url = `${this._url}/onderdelen`
		return this._magister.http.get(url)
		.then(res => res.json())
		.then(res => res.Items.map(e => new ActivityElement(this._magister, this, e)))
	}
}

export default Activity

import MagisterThing from './magisterThing'
import File from './file'
import { cleanHtmlContent, parseDate, toString } from './util'

/**
 * @extends MagisterThing
 */
class ActivityElement extends MagisterThing {
	/**
	 * @private
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, activity, raw) {
		super(magister)

		/**
		 * @type {String}
		 * @readonly
		 */
		this.id = toString(raw.Id)
		/**
		 * @type {String}
		 * @readonly
		 */
		this.title = raw.Titel
		/**
		 * @type {String}
		 * @readonly
		 */
		this.description = cleanHtmlContent(raw.Details)

		/**
		 * @type {Date}
		 * @readonly
		 */
		this.signinStart = parseDate(raw.StartInschrijfdatum)
		/**
		 * @type {Date}
		 * @readonly
		 */
		this.signinEnd = parseDate(raw.EindeInschrijfdatum)

		/**
		 * @type {Number}
		 * @readonly
		 */
		this.minParticipants = raw.MinAantalDeelnemers
		/**
		 * @type {Number}
		 * @readonly
		 */
		this.maxParticipants = raw.MaxAantalDeelnemers
		/**
		 * @type {Number}
		 * @readonly
		 */
		this.placesLeft = raw.AantalPlaatsenBeschikbaar

		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.signedup = raw.IsIngeschreven
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.ableToSignup = raw.IsOpInTeSchrijven
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.mandatorilySignedup = raw.IsVerplichtIngeschreven

		/**
		 * @type {File[]}
		 * @readonly
		 */
		this.attachments = (raw.Bijlagen || []).map(a => new File(this._magister, undefined, a))

		// REVIEW: do we want a reference to the parent activity?
		// this._activity = activity
		/**
		 * @type {String}
		 * @readonly
		 */
		this.activityId = toString(activity.id)
		/**
		 * @type {String}
		 * @private
		 * @readonly
		 */
		this._url = `${activity._url}/onderdelen/${this.id}`
	}

	/**
	 * @param {Boolean} val
	 * @return {ActivityElement}
	 */
	signup(val) {
		// TODO: handle errors, I have no idea how an error looks like, though.

		if (!this.ableToSignup || this.signedup === val) {
			return
		}

		return Promise.resolve().then(() => {
			const url = `${this._url}/inschrijvingen`

			if (val) {
				const payload = {
					persoonId: this._magister.profileInfo.id,
					activiteitId: Number.parseInt(this.activityId, 10),
					onderdeelId: Number.parseInt(this.id, 10),
				}

				return this._magister._privileges.needs('activiteiten', 'update')
				.then(() => this._magister.http.post(url, payload))
			} else {
				return this._magister._privileges.needs('activiteiten', 'delete')
				.then(() => this._magister.http.delete(url))
			}
		}).then(() => {
			this.signedup = val
			return this
		})
	}

	/**
	 * @private
	 * @return {Object}
	 */
	_toMagister() {
		return {
			Id: Number.parseInt(this.id, 10),
			Titel: this.title,
			Details: this.description,

			StartInschrijfdatum: this.signinStart.toJSON(),
			EindeInschrijfdatum: this.signinEnd.toJSON(),

			MinAantalDeelnemers: this.minParticipants,
			MaxAantalDeelnemers: this.maxParticipants,
			AantalPlaatsenBeschikbaar: this.placesLeft,

			IsIngeschreven: this.signedup,
			IsOpInTeSchrijven: this.ableToSignup,
			IsVerplichtIngeschreven: this.mandatorilySignedup,

			activiteitId: Number.parseInt(this.activityId, 10),
		}
	}
}

export default ActivityElement

import MagisterThing from './magisterThing'

/**
 * @extends MagisterThing
 */
class AddressInfo extends MagisterThing {
	/**
	 * @private
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @type {String}
		 * @readonly
		 */
		this.postalCode = raw.Postcode
		/**
		 * @type {String}
		 * @readonly
		 */
		this.street = raw.Straatnaam
		/**
		 * @type {Number}
		 * @readonly
		 */
		this.houseNumber = raw.Huisnummer
		/**
		 * String behind the `houseNumber` (eg 'A')
		 * @type {String}
		 * @readonly
		 */
		this.suffix = raw.Toevoeging
		/**
		 * @type {String}
		 * @readonly
		 */
		this.city = raw.Woonplaats
	}
}

export default AddressInfo

import _ from 'lodash'
import MagisterThing from './magisterThing'
import Person from './person'
import File from './file'
import { parseDate, toString } from './util'

/**
 * @extends MagisterThing
 */
class Appointment extends MagisterThing {
	/**
	 * @private
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @type {String}
		 * @readonly
		 */
		this.id = toString(raw.Id)
		/**
		 * @type {Date}
		 * @readonly
		 */
		this.start = parseDate(raw.Start)
		/**
		 * @type {Date}
		 * @readonly
		 */
		this.end = parseDate(raw.Einde)
		/**
		 * @type {Number}
		 * @readonly
		 */
		this.startBySchoolhour = raw.LesuurVan
		/**
		 * @type {Number}
		 * @readonly
		 */
		this.endBySchoolhour = raw.LesuurTotMet
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.isFullDay = raw.DuurtHeleDag
		/**
		 * @type {String}
		 * @readonly
		 */
		this.description = _.trim(raw.Omschrijving)
		/**
		 * @type {String}
		 * @readonly
		 */
		this.location = _.trim(raw.Lokatie)
		/**
		 * @type {String}
		 * @readonly
		 */
		this.status = raw.Status
		/**
		 * @type {String}
		 * @readonly
		 */
		this.type = raw.Type
		/**
		 * @type {String}
		 * @readonly
		 */
		this.displayType = raw.WeergaveType
		/**
		 * @type {String}
		 * @readonly
		 */
		this.content = raw.Inhoud
		/**
		 * @type {String}
		 * @readonly
		 */
		this.infoType = raw.InfoType
		/**
		 * @type {String}
		 * @readonly
		 */
		this.annotation = raw.Aantekening
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.isDone = raw.Afgerond
		/**
		 * @type {String[]}
		 * @readonly
		 */
		this.classes = raw.Vakken != null ? _.map(raw.Vakken, 'Naam') : [] // REVIEW: moeten we de key 'Naam' wel plucken?
		/**
		 * @type {String[]}
		 * @readonly
		 */
		this.teachers = raw.Docenten != null ? _.map(raw.Docenten, p => new Person(magister, p)) : []
		/**
		 * @type {String[]}
		 * @readonly
		 */
		this.classRooms = raw.Lokalen != null ? _.map(raw.Lokalen, 'Naam') : [] // REVIEW: moeten we de key 'Naam' wel plucken?
		/**
		 * @type {String[]}
		 * @readonly
		 */
		this.groups = raw.Groepen // ?
		/**
		 * @type {String}
		 * @readonly
		 */
		this.appointmentId = raw.OpdrachtId // REVIEW
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.hasAttachments = raw.HeeftBijlagen
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.isCancelled = [ 4, 5 ].includes(raw.Status)
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.isChanged = [ 3, 9, 10 ].includes(raw.Status)
		/**
		 * @type {AbsenceInfo}
		 * @readonly
		 */
		this.absenceInfo = undefined

		/**
		 * @type {String}
		 * @private
		 * @readonly
		 */
		this._url = `${magister._personUrl}/afspraken/${this.id}`
	}

	/**
	 * @return {Promise<File[]>}
	 */
	attachments() {
		if (!this.hasAttachments) {
			return Promise.resolve([])
		}

		return this._magister.http.get(this._url)
		.then(res => res.json())
		.then(res => {
			const attachments = res.Bijlagen
			const person = this.teachers[0]

			return attachments.map(raw => {
				const f = new File(this._magister, undefined, raw)
				f.addedBy = person
				return f
			})
		})
	}

	/**
	 * @return {Promise}
	 */
	remove() {
		if (this.type !== 1 && this.type !== 16) {
			return Promise.reject(new Error('Appointment not created by user'))
		}

		return this._magister._privileges.needs('afspraken', 'delete')
		.then(() => this._magister.http.delete(this._url))
	}

	/**
	 * Update the server to reflect the changes made on the properties of this
	 * Appointment instance.
	 * @return {Promise}
	 */
	saveChanges() {
		return this._magister._privileges.needs('afspraken', 'update')
		.then(() => this._magister.http.put(this._url, this._toMagister()))
		.then(() => undefined)
	}

	/**
	 * @private
	 * @return {Object}
	 */
	_toMagister() {
		return {
			Id: this.id,
			Start: this.start.toISOString(),
			Einde: this.end.toISOString(),
			LesuurVan: this.startBySchoolhour,
			LesuurTotMet: this.endBySchoolhour,
			DuurtHeleDag: this.isFullDay,
			Omschrijving: this.description,
			Lokatie: this.location,
			Status: this.status,
			Type: this.type,
			WeergaveType: this.displayType,
			Inhoud: this.content,
			InfoType: this.infoType,
			Aantekening: this.annotation,
			Afgerond: this.isDone,
			Vakken: this.classes,
			Docenten: this.teachers,
			Lokalen: this.classRooms,
			Groepen: this.groups,
			OpdrachtId: this.appointmentId,
			HeeftBijlagen: this.hasAttachments,
		}
	}
}

export default Appointment

import MagisterThing from './magisterThing'
import AssignmentVersion from './assignmentVersion'
import File from './file'
import Person from './person'
import { parseDate, toString } from './util'

/**
 * @extends MagisterThing
 */
class Assignment extends MagisterThing {
	/**
	 * @private
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @type {String}
		 * @readonly
		 */
		this.id = toString(raw.Id)
		/**
		 * @type {String}
		 * @readonly
		 */
		this.name = raw.Titel
		/**
		 * @type {String}
		 * @readonly
		 */
		this.description = raw.Omschrijving
		/**
		 * @type {Object}
		 * @readonly
		 */
		this.class = raw.Vak // TODO
		/**
		 * @type {Date}
		 * @readonly
		 */
		this.deadline = parseDate(raw.InleverenVoor)
		/**
		 * @type {Date}
		 * @readonly
		 */
		this.handedInOn = parseDate(raw.IngeleverdOp)
		/**
		 * @type {File[]}
		 * @readonly
		 */
		this.files = raw.Bijlagen.map(f => new File(magister, undefined, f))
		/**
		 * @type {Person[]}
		 * @readonly
		 */
		this.teachers = raw.Docenten != null ? raw.Docenten.map(p => new Person(magister, p)) : []
		/**
		 * @type {String[]}
		 * @readonly
		 */
		this.versionIds = raw.VersieNavigatieItems.map(v => toString(v.Id))
		/**
		 * @type {String}
		 * @readonly
		 */
		this.grade = raw.Beoordeling
		/**
		 * @type {Date}
		 * @readonly
		 */
		this.markedOn = parseDate(raw.BeoordeeldOp)
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.handInAgain = raw.OpnieuwInleveren
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.finished = raw.Afgesloten
		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.canHandIn = raw.MagInleveren
	}

	/**
	 * @return {Promise<AssignmentVersion[]>}
	 */
	versions() {
		const promises = this.versionIds.map(id => {
			const url = `${this._magister._personUrl}/opdrachten/versie/${id}`
			return this._magister.http.get(url)
			.then(res => res.json())
			.then(raw => new AssignmentVersion(this._magister, raw))
		})
		return Promise.all(promises)
	}
}

export default Assignment

import MagisterThing from './magisterThing'
import File from './file'
import { parseDate, toString } from './util'

/**
