		/**
		 * @type {Number}
		 * @readonly
		 */
		this.state = raw.Status
		/**
		 * @type {String}
		 * @readonly
		 */
		this.pupilMessage = raw.LeerlingOpmerking
		/**
		 * @type {String}
		 * @readonly
		 */
		this.teacherNotice = raw.DocentOpmerking
		/**
		 * @type {File[]}
		 * @readonly
		 */
		this.handedInFiles = raw.LeerlingBijlagen.map(f => new File(magister, undefined, f))
		/**
		 * @type {File[]}
		 * @readonly
		 */
