import _ from 'lodash'
import { Magister } from './magister'

/**
 * @private
 */
class MagisterThing {
	/**
	 * @param {Magister} magister
	 * @throws Error when `magister` isn't a {@link Magister} instance.
	 */
	constructor(magister) {
		if (!(magister instanceof Magister)) {
			throw new Error('`magister` must be a `Magister` instance')
		}

		/**
		 * @type {Magister}
		 * @private
		 * @readonly
		 */
		this._magister = magister
	}

	/**
	 * @return {Object}
	 */
	toJSON() {
		// REVIEW: do we want to omit all keys starting with '_'?
		return _.omit(this, [ '_magister' ])
	}
}

export default MagisterThing

/* global dedent */

import _ from 'lodash'
import MagisterThing from './magisterThing'
import Person from './person'
import File from './file'
import { cleanHtmlContent, parseDate, toString, cloneClassInstance } from './util'

/**
 * @extends MagisterThing
 */
class Message extends MagisterThing {
	/**
	 * @param {Magister} magister The Magister object you want to send this
	 * message from.
	 * @param {Object} [raw] Not needed, only used internally.
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @type {Boolean}
		 * @private
		 * @readonly
		 * @default true
		 */
		this._canSend = true
		/**
		 * @type {Number}
		 * @private
		 * @readonly
		 * @default 1
		 */
		this._type = 1
		/**
		 * @type {String}
		 * @readonly
		 * @default ''
		 */
		this.subject = ''
		/**
		 * If creating a message, this will be an empty string per default.
		 * When retrieving a message from Magister, this will be `undefined` per
		 * default, you should use `Message#fill` to get the body.
		 *
		 * @type {String}
		 * @readonly
		 * @default ''
		 */
		this.body = ''
		/**
		 * @type {Person[]}
		 * @readonly
		 * @default []
		 */
		this.recipients = []

		if (raw != null) {
			this._canSend = false
			this._type = raw.Soort
			this.subject = raw.Onderwerp
			this.body = undefined
			this.recipients = raw.Ontvangers.map(p => new Person(magister, p))

			/**
			 * @type {String}
			 * @readonly
			 */
			this.id = toString(raw.Id)
			/**
			 * @type {String}
			 * @readonly
			 */
			this.folderId = toString(raw.MapId)
			/**
			 * @type {Person}
			 * @readonly
			 */
			this.sender = new Person(magister, raw.Afzender)
			/**
			 * @type {Date}
			 * @readonly
			 */
			this.sendDate = parseDate(raw.VerstuurdOp)
			/**
			 * @type {Date}
			 * @readonly
			 */
			this.begin = parseDate(raw.Begin)
			/**
			 * @type {Date}
			 * @readonly
			 */
			this.end = parseDate(raw.Einde)
			/**
			 * @type {Boolean}
			 * @readonly
			 */
			this.isRead = raw.IsGelezen
			/**
			 * @type {Number}
			 * @readonly
			 */
			this.state = raw.Status
			/**
			 * @type {Boolean}
			 * @readonly
			 */
			this.isFlagged = raw.HeeftPrioriteit
			/**
			 * @type {String}
			 * @readonly
			 */
			this.summary = cleanHtmlContent(raw.IngekortBericht)

			/**
			 * This will be `undefined` per default, a fill using `Message#fill`
			 * is required to retrieve the attachments for this Message.
			 * @type {File[]}
			 * @readonly
			 * @default undefined
			 */
			this.attachments = undefined

			/**
			 * @type {String}
			 * @private
			 * @readonly
			 */
			this._url = `${magister._personUrl}/berichten/${this.id}`
		}
	}

	/**
	 * @type {String}
	 * @readonly
	 * @default 'message'
	 */
	get type() {
		switch (this._type) {
		case 1:  return 'message'
		case 2:  return 'alert'
		default: return 'unknown'
		}
	}

	/**
	 * @param {Person|Person[]} recipients
	 */
	addRecipient(recipients) {
		if (!Array.isArray(recipients)) {
			recipients = [ recipients ]
		}

		if (!recipients.every(x => x instanceof Person)) {
			throw new Error('`recipients` should be a Person or an Array of Persons')
		}

		this.recipients = this.recipients.concat(recipients)
	}

	createReplyMessage(content = '') {
		const message = cloneClassInstance(this)

		message.body = dedent`
			${content}

			---------------
			${this.toString()}
		`
		message.subject = `RE: ${this.subject}`
		message.recipients = [ this.sender ]

		message._type = 1
		message._canSend = true

		return message
	}

	createReplyToAllMessage(content = '') {
		const message = cloneClassInstance(this)

		message.body = dedent`
			${content}

			---------------
			${this.toString()}
		`
		message.subject = `RE: ${this.subject}`
		message.recipients =
			_.chain(this.recipients)
			.reject({ id: this._magister.profileInfo.id })
			.push(this.sender)
			.value()

		message._type = 1
		message._canSend = true

		return message
	}

	createForwardMessage(content = '') {
		const message = cloneClassInstance(this)

		message.body = dedent`
			${content}

			---------------
			${this.toString()}
		`
		message.subject = `FW: ${this.subject}`

		message._type = 1
		message._canSend = true

		return message
	}

	/**
	 * @param {Boolean} [fillPersons=false]
	 * @return {Promise<Message>}
	 */
	fill(fillPersons = false) {
		if (this._filled && (this._filledPersons || !fillPersons)) {
			return Promise.resolve(this)
		}

		const url = `${this._magister._personUrl}/berichten/${this.id}?berichtSoort=${this._type}`
		return this._magister.http.get(url)
		.then(res => res.json())
		.then(res => {
			this.body = cleanHtmlContent(res.Inhoud)
			this.attachments = (res.Bijlagen || []).map(a => new File(this._magister, undefined, a))

			if (fillPersons) {
				let promises = []

				// fill sender
				promises.push(
					this.sender.getFilled()
					.then(r => this.sender = r)
					.catch(() => this.sender)
				)

				// fill recipients
				promises = promises.concat(
					this.recipients.map(r => {
						return r.getFilled()
						.then(x => x)
						.catch(() => r)
					})
				)

				return Promise.all(promises)
			}
		})
		.then(() => {
			this._filled = true
			return this
		})
	}

	// REVIEW
	move(destination) {
		if (_.isObject(destination)) {
			destination = destination.id
		}

		if (this.folderId === destination) {
			return Promise.resolve(undefined)
		}

		this.folderId = destination
		return this.saveChanges()
	}

	/**
	 * @return {Promise<Error|undefined>}
	 */
	remove() {
		return this._magister._privileges.needs('berichten', 'delete')
		.then(() => this._magister.http.delete(this._url))
	}

	/**
	 * Update the server to reflect the changes made on the properties of this
	 * Message instance.
	 * @return {Promise<undefined>}
	 */
	saveChanges() {
		return this._magister._privileges.needs('berichten', 'update')
		.then(() => this._magister.http.put(this._url, this._toMagister()))
		.then(() => undefined)
	}

	/**
	 * @return {Promise<Message>}
	 */
	send() {
		const reject = message => Promise.reject(new Error(message))

		if (!this._canSend) {
			return reject('message is marked as unsendable')
		} else if (this.recipients.length === 0) {
			return reject('message doesn\'t have recipients')
		} else if (this.subject.length === 0) {
			return reject('subject is empty')
		}

		return this._magister._privileges.needs('berichten', 'create')
		.then(() => this._magister.http.post(
			`${this._magister._personUrl}/berichten`,
			this._toMagister()
		))
		.then(() => this)
	}

	toString() {
		return dedent`
			<b>Van:</b> ${this.sender.description}
			<b>Verzonden:</b> ${this.sendDate.toLocaleString()}
			<b>Aan:</b> ${this.recipients.map(p => p.description).join(', ')}
			<b>Onderwerp:</b> ${this.subject}

			${this.body}
		`
	}

	/**
	 * @private
	 * @return {Object}
	 */
	_toMagister() {
		return {
			Id: this.id,
			Inhoud: this.body,
			MapId: this.folderId, // number?
			Onderwerp: this.subject,
			Ontvangers: this.recipients.map(p => p._toMagister()),
			VerstuurdOp: this.sendDate || new Date(),
			Begin: this.begin,
			Einde: this.end,
			IsGelezen: this.isRead,
			Status: this.state,
			HeeftPrioriteit: this.isFlagged,
			Soort: this._type,
		}
	}
}

export default Message

import _ from 'lodash'
import MagisterThing from './magisterThing'
import Message from './message'
import { toString } from './util'

const MAX_LIMIT = 250

/**
 * @extends MagisterThing
 */
class MessageFolder extends MagisterThing {
	/**
	 * @private
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @type {String}
		 * @readonly
		 */
		this.id = toString(raw.Id)
		/**
		 * @type {String}
		 * @readonly
		 */
		this.name = raw.Naam
		/**
		 * @type {Number}
		 * @readonly
		 */
		this.unreadCount = raw.OngelezenBerichten
		/**
		 * @type {String}
		 * @readonly
		 */
		this.parentId = raw.ParentId

		/**
		 * @type {String}
		 * @readonly
		 */
		this.type = ({
			'postvak in': 'inbox',
			'verzonden items': 'sent',
			'verwijderde items': 'bin',
			'mededelingen': 'alerts',
		})[this.name.toLowerCase()] || 'unknown'
	}

	/**
	 * @param {Object} [options={}]
	 * 	@param {Number} [options.count=10] The limit of the amount of Messages to fetch. If `null`, all messages will be downloaded form the server.
	 * 	@param {Number} [options.skip=0] The amount of messages in front of the
	 * 	MessageFolder to skip.
	 * 	@param {String} [options.readState='all'] One of: 'all', 'read', 'unread'.
	 * 	@param {Boolean} [options.fill=true] Whether or not to call `fill` on every message.
	 * 	@param {Boolean} [options.fillPersons=false] Whether or not to download the users from the server. `options.fill` has to be true for this option to take effect.
	 * @return {Promise<Object>} { messages: Message[], totalCount: Number }
	 */
	messages({ count = 10, skip = 0, readState = 'all', fill = true, fillPersons = false } = {}) {
		if (![ 'all', 'read', 'unread' ].includes(readState)) {
			return Promise.reject(new Error('Invalid option to readState'))
		}

		let url = `${this._magister._personUrl}/berichten?mapId=${this.id}&top=${count}&skip=${skip}`
		if ([ 'read', 'unread' ].includes(readState)) {
			url += `&gelezen=${readState === 'read'}`
		}

		count = count === null ? Infinity : count
		if (count === 0) {
			return Promise.resolve([])
		} else if (count > MAX_LIMIT) {
			url += '&count=true'

			return this._magister._privileges.needs('berichten', 'read')
			.then(() => this._magister.http.get(url))
			.then(res => res.json())
			.then(res => res.TotalCount)
			.then(totalCount => {
				count = Math.min(count, totalCount)

				const promises = _.chain(count / MAX_LIMIT)
				.range()
				.map(n => this.messages({
					count: MAX_LIMIT,
					skip: n * MAX_LIMIT,
					readState,
					fill,
					fillPersons,
				}))
				.value()

				return Promise.all(promises).then(objects => ({
					messages: _(objects).map('messages').flatten().value(),
					totalCount,
				}))
			})
		}

		return this._magister._privileges.needs('berichten', 'read')
		.then(() => this._magister.http.get(url))
		.then(res => res.json())
		.then(res => {
			const messages = res.Items.map(m => new Message(this._magister, m))
			const promise = fill ?
				Promise.all(messages.map(m => m.fill(fillPersons))) :
				Promise.resolve(messages)

			return promise.then(messages => ({
				messages,
				totalCount: res.TotalCount,
			}))
		})
	}

	/**
	 * @override
	 * @return {Object}
	 */
	toJSON() {
		return _.omit(super.toJSON(), 'type')
	}
}

export default MessageFolder

import _ from 'lodash'
import MagisterThing from './magisterThing'
import { toString } from './util'

/**
 * @extends MagisterThing
 */
class Person extends MagisterThing {
	/**
	 * @private
	 * @param {Magister} magister
	 * @param {Object} raw
	 * @param {Number} type
	 */
	constructor(magister, raw, type) {
		super(magister)
		if (raw == null) {
			return
		}

		/**
		 * @type {Boolean}
		 * @private
		 * @default false
		 */
		this._filled = false

		/**
		 * @type {String}
		 * @readonly
		 */
		this.id = toString(raw.Id)
		/**
		 * @type {Number}
		 * @private
		 * @readonly
		 */
		this._type = type || raw.Type
		/**
		 * @type {String}
		 * @readonly
		 */
		this.firstName = raw.Voornaam
		/**
		 * @type {String}
		 * @readonly
		 */
		this.lastName = raw.Achternaam
		/**
		 * @type {String}
		 * @readonly
		 */
		this.namePrefix = raw.Tussenvoegsel
		/**
		 * @type {String}
		 * @readonly
		 */
		this.fullName = raw.Naam
		/**
		 * @type {String}
		 * @readonly
		 */
		this.description = raw.Omschrijving || raw.Naam || raw.naam
		/**
		 * @type {String}
		 * @readonly
		 */
		this.group = raw.Groep
		/**
		 * @type {String}
		 * @readonly
		 */
		this.teacherCode = raw.Docentcode
		/**
		 * @type {String}
		 * @readonly
		 */
		this.emailAddress = raw.Emailadres
	}

	get type() {
		switch (this._type) {
		case 1: return 'group'
		case 2: return 'study'
		case 3: return 'person'
		case 6: return 'location'
		case 8: return 'project'

		default: return 'unknown'
		}
	}

	set type(val) {
		if (!Number.isInteger(val)) {
			val = ({
				'group': 1,
				'study': 2,
				'person': 3,
				'location': 6,
				'project': 8,
			})[val]
		}

		if (![ 1, 2, 3, 6, 8 ].includes(val)) {
			throw new Error(`${val} is an invalid type value`)
		}

		this._type = val
	}

	/**
	 * @param {String} [type]
	 * @return {Promise<User>}
	 */
	getFilled(type) {
		if (this._filled) {
			return Promise.resolve(this)
		}

		return this._magister.persons(this.fullName, type)
		.then(persons => persons[0] || this)
	}

	/**
	 * @override
	 * @return {Object}
	 */
	toJSON() {
		const obj = _.omit(super.toJSON(), '_filled')
		delete obj._type
		return obj
	}

	/**
	 * @private
	 * @return {Object}
	 */
	_toMagister() {
		const obj = {}

		obj.Id = this.id
		obj.Type = this._type
		obj.Voornaam = this.firstName
		obj.Achternaam = this.lastName
		obj.Tussenvoegsel = this.namePrefix
		obj.Naam = this.fullName
		obj.Omschrijving = this.description
		obj.Groep = this.group
		obj.Docentcode = this.teacherCode
		obj.Emailadres = this.emailAddress

		return obj
	}
}

export default Person

/**
 * @private
 */
class Privileges {
	/**
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		/**
		 * @property _items
		 * @private
		 * @final
		 * @type {Object[]}
		 */
		this._items = raw
	}

	/**
	 * @method can
	 * @param {String} thing
	 * @param {String} action
	 * @return {Boolean}
	 */
	can(thing, action) {
		thing = thing.toLowerCase()
		action = action.toLowerCase()

		return this._items.some(x =>
			x.Naam.toLowerCase() === thing &&
			x.AccessType.some(a => a.toLowerCase() === action)
		)
	}

	/**
	 * @method needs
	 * @param {String} thing
	 * @param {String} action
	 * @return {Promise}
	 */
	needs(thing, action) {
		if (this.can(thing, action)) {
			return Promise.resolve()
		} else {
			return Promise.reject(new Error(`Account is not privileged to '${action}' '${thing}'`))
		}
	}
}

export default Privileges

import _ from 'lodash'
import MagisterThing from './magisterThing'
import AddressInfo from './addressInfo'
import ProfileSettings from './profileSettings'
import { parseDate, toString } from './util'

/**
 * @extends MagisterThing
 */
class ProfileInfo extends MagisterThing {
	/**
	 * @private
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @readonly
		 * @type {String}
		 */
		this.id = toString(raw.Id)
		/**
		 * @readonly
		 * @type {String}
		 */
		this.officialFirstNames = raw.OfficieleVoornamen
		/**
		 * @readonly
		 * @type {String}
		 */
		this.initials = raw.Voorletters
		/**
		 * @readonly
		 * @type {String}
		 */
		this.namePrefix = raw.Tussenvoegsel
		/**
		 * @readonly
		 * @type {String}
		 */
		this.officialSurname = raw.OfficieleAchternaam
		/**
		 * @readonly
		 * @type {String}
		 */
		this.birthSurname = raw.GeboorteAchternaam
		/**
		 * @readonly
		 * @type {String}
		 */
		this.birthNamePrefix = raw.GeboortenaamTussenvoegsel
		/**
		 * @readonly
		 * @type {Boolean}
		 */
		this.useBirthname = raw.GebruikGeboortenaam
		/**
		 * @readonly
		 * @type {String}
		 */
		this.firstName = raw.Roepnaam
		/**
		 * @readonly
		 * @type {String}
		 */
		this.lastName = raw.Achternaam
		/**
		 * @readonly
		 * @type {Date}
		 */
		this.birthDate = parseDate(raw.Geboortedatum)

		/**
		 * @readonly
		 * @type {Boolean}
		 */
		this.isChild = raw.ZichtbaarVoorOuder != null
		/**
		 * `undefined` when `this.isChild` is `false`.
		 * @readonly
		 * @type {Boolean|undefined}
		 */
		this.isVisibleForParent = raw.ZichtbaarVoorOuder
	}

	/**
	 * @param {Boolean} [useBirthname=this.useBirthname]
	 * @return String
	 */
	getFullName(useBirthname = this.useBirthname) {
		return _(
			useBirthname ?
				[ this.officialFirstNames, this.birthNamePrefix, this.birthSurname ] :
				[ this.firstName, this.namePrefix, this.lastName ]
		).compact().join(' ')
	}

	/**
	 * Opens a stream to the profile picture of the current user with the given
	 * options.
	 *
	 * @param {Number} [width=640] The width of the picture.
	 * @param {Number} [height=640] The height of the picture.
	 * @param {Boolean} [crop=false] Whether or not to crop the image.
	 * @return {Promise<Stream>}
	 */
	getProfilePicture(width = 640, height = 640, crop = false) {
		const url = `${this._magister._personUrl}/foto?width=${width}&height=${height}&crop=${crop}`
		return this._magister.http.get(url).then(res => res.body)
	}

	/**
	 * @return {Promise<Error|AddressInfo>}
	 */
	address() {
		const url = `${this._magister._personUrl}/adresprofiel`

		return this._magister._privileges.needs('profiel', 'read')
		.then(() => this._magister.http.get(url))
		.then(res => res.json())
		.then(raw => new AddressInfo(this._magister, raw))
	}

	/**
	 * @return {Promise<Error|ProfileSettings>}
	 */
	settings() {
		const url = `${this._magister._personUrl}/profiel`

		return this._magister._privileges.needs('profiel', 'read')
		.then(() => this._magister.http.get(url))
		.then(res => res.json())
		.then(raw => new ProfileSettings(this._magister, raw))
	}
}

export default ProfileInfo

import MagisterThing from './magisterThing'

/**
 * @extends MagisterThing
 */
class ProfileSettings extends MagisterThing {
	/**
	 * @private
	 * @param {Magister} magister
	 * @param {Object} raw
	 */
	constructor(magister, raw) {
		super(magister)

		/**
		 * @type {Boolean}
		 */
		this.redirectMagisterMessages = raw.EloBerichtenDoorsturen
		/**
		 * @type {String}
		 */
		this.emailAddress = raw.EmailAdres
		/**
		 * @type {String}
		 */
		this.mobileNumber = raw.Mobiel
	}

	/**
	 * Update the server to reflect the changes made on the properties of this
	 * ProfileSettings instance.
	 * @return {Promise<Error|undefined>}
	 */
	async saveChanges() {
		await this._magister._privileges.needs('profiel', 'update')

		const url = `${this._magister._personUrl}/profiel`
		await this._magister.http.put(url, this._toMagister())
	}

	/**
	 * Change the user password,
	 * seperate function because requires verification.
	 *
	 * @param {String} changed
	 * @param {String} [original] - Not required, defaults to password set on auth
	 * @return {Promise}
	 */
	async changePassword(changed, original) {
		original = original || this._magister._options.password
		const schoolUrl = this._magister.school.url

		await this._magister._privileges.needs('wachtwoordwijzigen', 'update')
		const profile = await this._magister.http.post(`${schoolUrl}/api/sessies/huidige/valideer`, {
			'wachtwoord': original,
		}).then(res => res.json())

		if (profile.isVerified) {
			const selfUrl = profile.links.account.href
			const status = await this._magister.http.put(`${schoolUrl}/${selfUrl}/wachtwoord`, {
				'wachtwoord': changed,
				'wachtwoordControle': original,
			}).then(res => res.status)

			if (status !== 204) {
				throw new Error(`Changing password failed with status code ${status}!`)
			}
		} else {
			throw new Error('Original password incorrect')
		}
	}

	/**
	 * @private
	 * @return {Object}
	 */
	_toMagister() {
		return {
			EloBerichtenDoorsturen: this.redirectMagisterMessages,
			EmailAdres: this.emailAddress,
			Mobiel: this.mobileNumber,
		}
	}
}

export default ProfileSettings

